export function increment(id) {
    return {
        type: '@cart/incremented',
        id, 
    }
}


export function decrement(id) {
    return {
        type: '@cart/decremented', // tipo de envio (ação (action))
        id, // recebe um id por livro
    }
}

// exporta add function com direito ao parâmetro id para cada livro
export function add(id) {
    return {
        type: 'add',    // tipo de envio (ação (action))
        id // recebe um id por livro
    }
}

// exporta addSuccess com direito ao parâmetro product (um objeto JSON)
export function addSuccess(product) {
    return {
        type: '@cart/addSuccess',    // tipo de envio (ação (action))
        product, // recebe objeto literal (listas) em JSON
    }
}

// exporta cartAmountUpdateSuccess responsável por atualizar a quantia de cada livro
export function cartAmountUpdateSuccess(id) {
    return {
        type: '@cart/amountSuccess',    // tipo de envio (ação (action))
        id, // recebe um id por livro
    }
}

// exporta cartRemove responsável por remover um item por id ao carrinho
export function cartRemove(id) {
    return {
        type: '@cart/removeItem',    // tipo de envio (ação (action))
        id, // recebe um id por livro
    }
}

// exporta cartAmountUpdate responsável por remover quantia de item por id ao carrinho
export function cartAmountUpdate(id) {
    return {
        type: '@cart/removeAmount',    // tipo de envio (ação (action))
        id, // recebe um id por livro
    }
}

// exporta cartClearUpdate responsável por limpar o carrinho
export function cartClearUpdate() {
    return {
        type: '@cart/clear',    // tipo de envio (ação (action))
    }
}

// exporta cartClearAmountUpdate responsável por limpar todas as quantias de item ao carrinho
export function cartClearAmountUpdate() {
    return {
        type: '@cart/clearAmount',    // tipo de envio (ação (action))
    }
}
